contracts/StorageProof.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title StorageProof
 * @dev On-chain storage proof challenge and verification system.
 *      Backend issues challenges, nodes respond with proofs,
 *      and verification results are recorded immutably on-chain.
 */
contract StorageProof is Ownable {

    enum ChallengeStatus { Pending, Verified, Failed, Expired }

    struct Challenge {
        bytes32 challengeId;
        bytes32 nodeId;
        bytes32 chunkHash;
        uint256 offset;
        uint256 readBytes;
        uint256 createdAt;
        uint256 expiresAt;
        ChallengeStatus status;
        bytes32 expectedHash;   // set by backend after verification
        bytes32 submittedHash;  // proof submitted by node
    }

    // Storage
    mapping(bytes32 => Challenge) public challenges;
    bytes32[] public allChallengeIds;

    // Node proof stats
    mapping(bytes32 => uint256) public nodeProofsPassed;
    mapping(bytes32 => uint256) public nodeProofsFailed;
    mapping(bytes32 => uint256) public nodeLastChallenge;

    // Challenge configuration
    uint256 public challengeExpirySeconds = 300; // 5 minutes
    uint256 public maxChallengesPerBatch = 10;

    // Authorized executors
    mapping(address => bool) public isExecutor;

    // Events
    event ChallengeIssued(bytes32 indexed challengeId, bytes32 indexed nodeId, bytes32 chunkHash, uint256 offset);
    event ProofSubmitted(bytes32 indexed challengeId, bytes32 indexed nodeId, bytes32 proofHash);
    event ProofVerified(bytes32 indexed challengeId, bytes32 indexed nodeId, bool passed);
    event ChallengeExpired(bytes32 indexed challengeId, bytes32 indexed nodeId);
    event ExecutorUpdated(address indexed executor, bool allowed);

    modifier onlyExecutor() {
        require(isExecutor[msg.sender] || msg.sender == owner(), "Not an executor");
        _;
    }

    constructor() Ownable(msg.sender) {
        isExecutor[msg.sender] = true;
    }

    /**
     * @dev Issue a batch of storage proof challenges
     */
    function issueChallenges(
        bytes32[] calldata challengeIds,
        bytes32[] calldata nodeIds,
        bytes32[] calldata chunkHashes,
        uint256[] calldata offsets,
        uint256[] calldata readBytesArr
    ) external onlyExecutor {
        require(
            challengeIds.length == nodeIds.length &&
            challengeIds.length == chunkHashes.length &&
            challengeIds.length == offsets.length &&
            challengeIds.length == readBytesArr.length,
            "Array length mismatch"
        );
        require(challengeIds.length <= maxChallengesPerBatch, "Too many challenges");

        uint256 expiresAt = block.timestamp + challengeExpirySeconds;

        for (uint256 i = 0; i < challengeIds.length; i++) {
            require(challenges[challengeIds[i]].createdAt == 0, "Challenge already exists");

            challenges[challengeIds[i]] = Challenge({
                challengeId: challengeIds[i],
                nodeId: nodeIds[i],
                chunkHash: chunkHashes[i],
                offset: offsets[i],
                readBytes: readBytesArr[i],
                createdAt: block.timestamp,
                expiresAt: expiresAt,
                status: ChallengeStatus.Pending,
                expectedHash: bytes32(0),
                submittedHash: bytes32(0)
            });

            allChallengeIds.push(challengeIds[i]);
            nodeLastChallenge[nodeIds[i]] = block.timestamp;

            emit ChallengeIssued(challengeIds[i], nodeIds[i], chunkHashes[i], offsets[i]);
        }
    }

    /**
     * @dev Submit proof responses for challenges
     */
    function submitProofs(
        bytes32[] calldata challengeIds,
        bytes32[] calldata proofHashes
    ) external onlyExecutor {
        require(challengeIds.length == proofHashes.length, "Array length mismatch");

        for (uint256 i = 0; i < challengeIds.length; i++) {
            Challenge storage c = challenges[challengeIds[i]];
            require(c.createdAt > 0, "Challenge not found");
            require(c.status == ChallengeStatus.Pending, "Challenge not pending");

            if (block.timestamp > c.expiresAt) {
                c.status = ChallengeStatus.Expired;
                nodeProofsFailed[c.nodeId]++;
                emit ChallengeExpired(challengeIds[i], c.nodeId);
                continue;
            }

            c.submittedHash = proofHashes[i];
            emit ProofSubmitted(challengeIds[i], c.nodeId, proofHashes[i]);
        }
    }

    /**
     * @dev Verify submitted proofs against expected hashes
     *      Called by the backend after independently computing the expected hash
     */
    function verifyProofs(
        bytes32[] calldata challengeIds,
        bytes32[] calldata expectedHashes
    ) external onlyExecutor {
        require(challengeIds.length == expectedHashes.length, "Array length mismatch");

        for (uint256 i = 0; i < challengeIds.length; i++) {
            Challenge storage c = challenges[challengeIds[i]];
            require(c.createdAt > 0, "Challenge not found");

            if (c.status != ChallengeStatus.Pending) continue;

            c.expectedHash = expectedHashes[i];

            bool passed = (c.submittedHash == expectedHashes[i] && c.submittedHash != bytes32(0));

            if (passed) {
                c.status = ChallengeStatus.Verified;
                nodeProofsPassed[c.nodeId]++;
            } else {
                c.status = ChallengeStatus.Failed;
                nodeProofsFailed[c.nodeId]++;
            }

            emit ProofVerified(challengeIds[i], c.nodeId, passed);
        }
    }

    /**
     * @dev Expire pending challenges that have passed their deadline
     */
    function expireChallenges(bytes32[] calldata challengeIds) external onlyExecutor {
        for (uint256 i = 0; i < challengeIds.length; i++) {
            Challenge storage c = challenges[challengeIds[i]];
            if (c.status == ChallengeStatus.Pending && block.timestamp > c.expiresAt) {
                c.status = ChallengeStatus.Expired;
                nodeProofsFailed[c.nodeId]++;
                emit ChallengeExpired(challengeIds[i], c.nodeId);
            }
        }
    }

    // --- View Functions ---

    function getChallenge(bytes32 challengeId) external view returns (Challenge memory) {
        return challenges[challengeId];
    }

    function getNodeProofStats(bytes32 nodeId) external view returns (
        uint256 passed,
        uint256 failed,
        uint256 lastChallenge,
        uint256 successRate // basis points (0-10000)
    ) {
        passed = nodeProofsPassed[nodeId];
        failed = nodeProofsFailed[nodeId];
        lastChallenge = nodeLastChallenge[nodeId];
        uint256 total = passed + failed;
        successRate = total > 0 ? (passed * 10000) / total : 0;
    }

    function getTotalChallenges() external view returns (uint256) {
        return allChallengeIds.length;
    }

    // --- Admin ---

    function setChallengeExpiry(uint256 seconds_) external onlyOwner {
        challengeExpirySeconds = seconds_;
    }

    function setMaxBatchSize(uint256 maxBatch) external onlyOwner {
        maxChallengesPerBatch = maxBatch;
    }

    function setExecutor(address executor, bool allowed) external onlyOwner {
        isExecutor[executor] = allowed;
        emit ExecutorUpdated(executor, allowed);
    }
}

contracts/StorageRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title StorageRegistry
 * @dev On-chain registry of storage nodes and their allocated capacity.
 *      Records node registrations, capacity pledges, and status updates.
 *      Serves as the source of truth for which nodes participate in storage.
 */
contract StorageRegistry is Ownable, Pausable {

    enum NodeStatus { Inactive, Active, Suspended }

    struct NodeInfo {
        bytes32 nodeId;
        address operator;
        uint256 capacityGB;
        uint256 usedGB;
        string nodeClass;       // "lite", "standard", "full", "agent"
        uint256 registeredAt;
        uint256 lastHeartbeat;
        NodeStatus status;
        uint256 proofSuccessCount;
        uint256 proofFailCount;
    }

    // Storage
    mapping(bytes32 => NodeInfo) public nodes;
    mapping(address => bytes32[]) public operatorNodes;
    bytes32[] public allNodeIds;

    uint256 public totalCapacityGB;
    uint256 public totalUsedGB;
    uint256 public totalActiveNodes;

    // Merkle root registry: fileKey => merkleRoot
    mapping(bytes32 => bytes32) public fileMerkleRoots;
    mapping(bytes32 => uint256) public fileRegisteredAt;

    // Authorized executors (backend services)
    mapping(address => bool) public isExecutor;

    // Events
    event NodeRegistered(bytes32 indexed nodeId, address indexed operator, uint256 capacityGB, string nodeClass);
    event NodeUpdated(bytes32 indexed nodeId, uint256 capacityGB, uint256 usedGB);
    event NodeStatusChanged(bytes32 indexed nodeId, NodeStatus status);
    event HeartbeatRecorded(bytes32 indexed nodeId, uint256 timestamp);
    event MerkleRootRecorded(bytes32 indexed fileKey, bytes32 merkleRoot, uint256 timestamp);
    event ExecutorUpdated(address indexed executor, bool allowed);

    modifier onlyExecutor() {
        require(isExecutor[msg.sender] || msg.sender == owner(), "Not an executor");
        _;
    }

    constructor() Ownable(msg.sender) {
        isExecutor[msg.sender] = true;
    }

    /**
     * @dev Register a new storage node
     */
    function registerNode(
        bytes32 nodeId,
        address operator,
        uint256 capacityGB,
        string calldata nodeClass
    ) external onlyExecutor whenNotPaused {
        require(nodes[nodeId].registeredAt == 0, "Node already registered");
        require(operator != address(0), "Invalid operator");
        require(capacityGB > 0, "Capacity must be > 0");

        nodes[nodeId] = NodeInfo({
            nodeId: nodeId,
            operator: operator,
            capacityGB: capacityGB,
            usedGB: 0,
            nodeClass: nodeClass,
            registeredAt: block.timestamp,
            lastHeartbeat: block.timestamp,
            status: NodeStatus.Active,
            proofSuccessCount: 0,
            proofFailCount: 0
        });

        operatorNodes[operator].push(nodeId);
        allNodeIds.push(nodeId);
        totalCapacityGB += capacityGB;
        totalActiveNodes++;

        emit NodeRegistered(nodeId, operator, capacityGB, nodeClass);
    }

    /**
     * @dev Update node capacity and usage
     */
    function updateNode(
        bytes32 nodeId,
        uint256 capacityGB,
        uint256 usedGB
    ) external onlyExecutor {
        NodeInfo storage node = nodes[nodeId];
        require(node.registeredAt > 0, "Node not registered");

        totalCapacityGB = totalCapacityGB - node.capacityGB + capacityGB;
        totalUsedGB = totalUsedGB - node.usedGB + usedGB;

        node.capacityGB = capacityGB;
        node.usedGB = usedGB;

        emit NodeUpdated(nodeId, capacityGB, usedGB);
    }

    /**
     * @dev Record a heartbeat from a node
     */
    function recordHeartbeat(bytes32 nodeId) external onlyExecutor {
        NodeInfo storage node = nodes[nodeId];
        require(node.registeredAt > 0, "Node not registered");

        node.lastHeartbeat = block.timestamp;
        emit HeartbeatRecorded(nodeId, block.timestamp);
    }

    /**
     * @dev Batch record heartbeats
     */
    function recordHeartbeatBatch(bytes32[] calldata nodeIds) external onlyExecutor {
        for (uint256 i = 0; i < nodeIds.length; i++) {
            if (nodes[nodeIds[i]].registeredAt > 0) {
                nodes[nodeIds[i]].lastHeartbeat = block.timestamp;
                emit HeartbeatRecorded(nodeIds[i], block.timestamp);
            }
        }
    }

    /**
     * @dev Change node status (activate, suspend, deactivate)
     */
    function setNodeStatus(bytes32 nodeId, NodeStatus status) external onlyExecutor {
        NodeInfo storage node = nodes[nodeId];
        require(node.registeredAt > 0, "Node not registered");

        if (node.status == NodeStatus.Active && status != NodeStatus.Active) {
            totalActiveNodes--;
        } else if (node.status != NodeStatus.Active && status == NodeStatus.Active) {
            totalActiveNodes++;
        }

        node.status = status;
        emit NodeStatusChanged(nodeId, status);
    }

    /**
     * @dev Record proof results for a node
     */
    function recordProofResult(bytes32 nodeId, bool passed) external onlyExecutor {
        NodeInfo storage node = nodes[nodeId];
        require(node.registeredAt > 0, "Node not registered");

        if (passed) {
            node.proofSuccessCount++;
        } else {
            node.proofFailCount++;
        }
    }

    /**
     * @dev Record a file's Merkle root on-chain for immutable verification
     */
    function recordMerkleRoot(bytes32 fileKey, bytes32 merkleRoot) external onlyExecutor {
        fileMerkleRoots[fileKey] = merkleRoot;
        fileRegisteredAt[fileKey] = block.timestamp;
        emit MerkleRootRecorded(fileKey, merkleRoot, block.timestamp);
    }

    /**
     * @dev Verify a file's Merkle root
     */
    function verifyMerkleRoot(bytes32 fileKey, bytes32 merkleRoot) external view returns (bool) {
        return fileMerkleRoots[fileKey] == merkleRoot;
    }

    // --- View Functions ---

    function getNode(bytes32 nodeId) external view returns (NodeInfo memory) {
        return nodes[nodeId];
    }

    function getOperatorNodes(address operator) external view returns (bytes32[] memory) {
        return operatorNodes[operator];
    }

    function getTotalNodes() external view returns (uint256) {
        return allNodeIds.length;
    }

    function getNetworkStats() external view returns (
        uint256 _totalNodes,
        uint256 _activeNodes,
        uint256 _totalCapacityGB,
        uint256 _totalUsedGB
    ) {
        return (allNodeIds.length, totalActiveNodes, totalCapacityGB, totalUsedGB);
    }

    // --- Admin ---

    function setExecutor(address executor, bool allowed) external onlyOwner {
        isExecutor[executor] = allowed;
        emit ExecutorUpdated(executor, allowed);
    }

    function pause() external onlyOwner { _pause(); }
    function unpause() external onlyOwner { _unpause(); }
}

contracts/StorageRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title StorageRewards
 * @dev On-chain reward distribution for storage node operators.
 *      Calculates rewards based on uptime, storage capacity, proof success rate,
 *      and node class weight. Supports slashing for misbehavior.
 *
 *      Reward pool is funded by the contract owner (admin) via fundPool().
 *      Nodes claim accumulated rewards through claimReward().
 */
contract StorageRewards is Ownable, ReentrancyGuard, Pausable {

    struct RewardInfo {
        uint256 pendingReward;
        uint256 totalClaimed;
        uint256 lastCalculation;
        uint256 slashCount;
        bool slashed; // currently slashed (no rewards)
    }

    // Storage
    mapping(bytes32 => RewardInfo) public rewards;
    bytes32[] public rewardedNodes;

    // Reward configuration
    uint256 public baseRewardPerDay = 1 ether;       // 1 VCN base per day
    uint256 public storageBonusMultiplier = 100;      // per GB, basis points (1%)
    uint256 public proofBonusMultiplier = 500;        // for 100% proof rate, basis points (5%)
    uint256 public slashPenaltyPercent = 50;          // 50% of pending rewards slashed
    uint256 public slashCooldownDays = 7;

    // Pool
    uint256 public rewardPoolBalance;
    uint256 public totalDistributed;

    // Authorized executors
    mapping(address => bool) public isExecutor;

    // Node operator wallets (nodeId => wallet)
    mapping(bytes32 => address) public nodeWallets;

    // Events
    event RewardAccrued(bytes32 indexed nodeId, uint256 amount, uint256 totalPending);
    event RewardClaimed(bytes32 indexed nodeId, address indexed wallet, uint256 amount);
    event NodeSlashed(bytes32 indexed nodeId, uint256 penaltyAmount, string reason);
    event PoolFunded(address indexed funder, uint256 amount, uint256 newBalance);
    event NodeWalletSet(bytes32 indexed nodeId, address wallet);
    event ExecutorUpdated(address indexed executor, bool allowed);

    modifier onlyExecutor() {
        require(isExecutor[msg.sender] || msg.sender == owner(), "Not an executor");
        _;
    }

    constructor() Ownable(msg.sender) {
        isExecutor[msg.sender] = true;
    }

    /**
     * @dev Fund the reward pool with native VCN
     */
    function fundPool() external payable {
        require(msg.value > 0, "Must send VCN");
        rewardPoolBalance += msg.value;
        emit PoolFunded(msg.sender, msg.value, rewardPoolBalance);
    }

    /**
     * @dev Set the wallet address for a node
     */
    function setNodeWallet(bytes32 nodeId, address wallet) external onlyExecutor {
        require(wallet != address(0), "Invalid wallet");
        nodeWallets[nodeId] = wallet;

        // Initialize reward tracker if first time
        if (rewards[nodeId].lastCalculation == 0) {
            rewards[nodeId].lastCalculation = block.timestamp;
            rewardedNodes.push(nodeId);
        }

        emit NodeWalletSet(nodeId, wallet);
    }

    /**
     * @dev Accrue rewards for a node based on its contribution
     * @param nodeId The node identifier
     * @param uptimeHours Node uptime in hours since last calculation
     * @param capacityGB Allocated storage capacity
     * @param proofSuccessRate Success rate in basis points (0-10000)
     * @param weightBp Node class weight in basis points (100 = 1x)
     */
    function accrueReward(
        bytes32 nodeId,
        uint256 uptimeHours,
        uint256 capacityGB,
        uint256 proofSuccessRate,
        uint256 weightBp
    ) external onlyExecutor whenNotPaused {
        RewardInfo storage info = rewards[nodeId];

        // Skip if currently slashed
        if (info.slashed) return;

        // Initialize if needed
        if (info.lastCalculation == 0) {
            info.lastCalculation = block.timestamp;
            rewardedNodes.push(nodeId);
        }

        // Calculate reward:
        // reward = baseRate * weight * (1 + storageBonus + proofBonus) * (uptimeHours / 24)
        uint256 dayFraction = (uptimeHours * 1e18) / 24;
        uint256 weightedBase = (baseRewardPerDay * weightBp) / 10000;

        // Storage bonus: capacityGB * storageBonusMultiplier / 10000
        uint256 storageBonus = (capacityGB * storageBonusMultiplier);

        // Proof bonus: proofSuccessRate * proofBonusMultiplier / 10000
        uint256 proofBonus = (proofSuccessRate * proofBonusMultiplier) / 10000;

        // Total multiplier = 10000 (base) + storageBonus + proofBonus
        uint256 totalMultiplier = 10000 + storageBonus + proofBonus;

        uint256 reward = (weightedBase * totalMultiplier * dayFraction) / (10000 * 1e18);

        if (reward > 0) {
            info.pendingReward += reward;
            info.lastCalculation = block.timestamp;
            emit RewardAccrued(nodeId, reward, info.pendingReward);
        }
    }

    /**
     * @dev Batch accrue rewards for multiple nodes
     */
    function accrueRewardBatch(
        bytes32[] calldata nodeIds,
        uint256[] calldata uptimeHours,
        uint256[] calldata capacityGBs,
        uint256[] calldata proofRates,
        uint256[] calldata weights
    ) external onlyExecutor whenNotPaused {
        require(
            nodeIds.length == uptimeHours.length &&
            nodeIds.length == capacityGBs.length &&
            nodeIds.length == proofRates.length &&
            nodeIds.length == weights.length,
            "Array length mismatch"
        );

        for (uint256 i = 0; i < nodeIds.length; i++) {
            RewardInfo storage info = rewards[nodeIds[i]];
            if (info.slashed) continue;

            if (info.lastCalculation == 0) {
                info.lastCalculation = block.timestamp;
                rewardedNodes.push(nodeIds[i]);
            }

            uint256 dayFraction = (uptimeHours[i] * 1e18) / 24;
            uint256 weightedBase = (baseRewardPerDay * weights[i]) / 10000;
            uint256 storageBonus = capacityGBs[i] * storageBonusMultiplier;
            uint256 proofBonus = (proofRates[i] * proofBonusMultiplier) / 10000;
            uint256 totalMultiplier = 10000 + storageBonus + proofBonus;
            uint256 reward = (weightedBase * totalMultiplier * dayFraction) / (10000 * 1e18);

            if (reward > 0) {
                info.pendingReward += reward;
                info.lastCalculation = block.timestamp;
                emit RewardAccrued(nodeIds[i], reward, info.pendingReward);
            }
        }
    }

    /**
     * @dev Claim pending rewards for a node
     */
    function claimReward(bytes32 nodeId) external nonReentrant whenNotPaused {
        address wallet = nodeWallets[nodeId];
        require(wallet != address(0), "No wallet set");
        require(msg.sender == wallet || isExecutor[msg.sender] || msg.sender == owner(), "Not authorized");

        RewardInfo storage info = rewards[nodeId];
        uint256 amount = info.pendingReward;
        require(amount > 0, "No pending rewards");
        require(rewardPoolBalance >= amount, "Insufficient pool balance");

        info.pendingReward = 0;
        info.totalClaimed += amount;
        rewardPoolBalance -= amount;
        totalDistributed += amount;

        (bool success, ) = wallet.call{value: amount}("");
        require(success, "Transfer failed");

        emit RewardClaimed(nodeId, wallet, amount);
    }

    /**
     * @dev Slash a node's rewards for misbehavior
     */
    function slashNode(bytes32 nodeId, string calldata reason) external onlyExecutor {
        RewardInfo storage info = rewards[nodeId];

        uint256 penalty = (info.pendingReward * slashPenaltyPercent) / 100;
        info.pendingReward -= penalty;
        info.slashCount++;
        info.slashed = true;

        // Return penalty to pool
        rewardPoolBalance += penalty;

        emit NodeSlashed(nodeId, penalty, reason);
    }

    /**
     * @dev Unslash a node (restore rewards eligibility)
     */
    function unslashNode(bytes32 nodeId) external onlyExecutor {
        rewards[nodeId].slashed = false;
        rewards[nodeId].lastCalculation = block.timestamp;
    }

    // --- View Functions ---

    function getRewardInfo(bytes32 nodeId) external view returns (RewardInfo memory) {
        return rewards[nodeId];
    }

    function calculatePendingReward(bytes32 nodeId) external view returns (uint256) {
        return rewards[nodeId].pendingReward;
    }

    function getPoolStats() external view returns (
        uint256 poolBalance,
        uint256 distributed,
        uint256 totalNodes
    ) {
        return (rewardPoolBalance, totalDistributed, rewardedNodes.length);
    }

    // --- Admin ---

    function setBaseReward(uint256 rewardPerDay) external onlyOwner {
        baseRewardPerDay = rewardPerDay;
    }

    function setStorageBonusMultiplier(uint256 multiplier) external onlyOwner {
        storageBonusMultiplier = multiplier;
    }

    function setProofBonusMultiplier(uint256 multiplier) external onlyOwner {
        proofBonusMultiplier = multiplier;
    }

    function setSlashPenalty(uint256 percent) external onlyOwner {
        require(percent <= 100, "Invalid percent");
        slashPenaltyPercent = percent;
    }

    function setExecutor(address executor, bool allowed) external onlyOwner {
        isExecutor[executor] = allowed;
        emit ExecutorUpdated(executor, allowed);
    }

    function pause() external onlyOwner { _pause(); }
    function unpause() external onlyOwner { _unpause(); }

    // Emergency withdraw
    function emergencyWithdraw() external onlyOwner {
        uint256 bal = address(this).balance;
        (bool success, ) = owner().call{value: bal}("");
        require(success, "Withdraw failed");
    }

    receive() external payable {
        rewardPoolBalance += msg.value;
    }
}

contracts/TimeLockAgent.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title TimeLockAgent
 * @dev A contract to schedule VCN native token transfers securely.
 * The user deposits funds which are released by an authorized executor after a specific unlock time.
 */
contract TimeLockAgent is ReentrancyGuard, Pausable, Ownable {
    // Defines the possible states of a scheduled transfer
    enum Status { Waiting, Executed, Cancelled, Expired }

    struct ScheduledTransfer {
        uint256 id;
        address creator;
        address to;
        uint256 amount;
        uint256 unlockTime;
        Status status;
        uint256 createdAt;
    }

    // State Variables
    uint256 public nextScheduleId;
    uint256 public gracePeriod = 7 days; // Time after unlockTime when it can be expired
    mapping(uint256 => ScheduledTransfer) public transfers;

    // Whitelisted addresses that can trigger execution
    mapping(address => bool) public isExecutor;

    // Events
    event TransferScheduled(uint256 indexed scheduleId, address indexed creator, address indexed to, uint256 amount, uint256 unlockTime);
    event TransferExecuted(uint256 indexed scheduleId, address indexed executor, uint256 executedAt);
    event TransferCancelled(uint256 indexed scheduleId, uint256 cancelledAt);
    event TransferExpired(uint256 indexed scheduleId, uint256 expiredAt);
    event ExecutorUpdated(address indexed executor, bool allowed);
    event GracePeriodUpdated(uint256 newGracePeriod);

    // Modifiers
    modifier onlyExecutor() {
        require(isExecutor[msg.sender] || msg.sender == owner(), "TimeLockAgent: Caller is not an executor");
        _;
    }

    modifier onlyCreator(uint256 scheduleId) {
        require(transfers[scheduleId].creator == msg.sender, "TimeLockAgent: Caller is not the creator");
        _;
    }

    modifier exists(uint256 scheduleId) {
        require(transfers[scheduleId].createdAt != 0, "TimeLockAgent: Transfer does not exist");
        _;
    }

    modifier inStatus(uint256 scheduleId, Status status) {
        require(transfers[scheduleId].status == status, "TimeLockAgent: Invalid status");
        _;
    }

    constructor() Ownable(msg.sender) {
        // Deployer is automatically an executor
        isExecutor[msg.sender] = true;
        emit ExecutorUpdated(msg.sender, true);
    }

    /**
     * @dev Schedules a native token transfer.
     * @param to The recipient address.
     * @param unlockTime The unix timestamp when funds can be released.
     */
    function scheduleTransferNative(address to, uint256 unlockTime) external payable whenNotPaused returns (uint256) {
        require(msg.value > 0, "TimeLockAgent: Amount must be greater than 0");
        require(to != address(0), "TimeLockAgent: Invalid recipient");
        require(unlockTime > block.timestamp, "TimeLockAgent: Unlock time must be in future");

        uint256 scheduleId = nextScheduleId++;

        transfers[scheduleId] = ScheduledTransfer({
            id: scheduleId,
            creator: msg.sender,
            to: to,
            amount: msg.value,
            unlockTime: unlockTime,
            status: Status.Waiting,
            createdAt: block.timestamp
        });

        emit TransferScheduled(scheduleId, msg.sender, to, msg.value, unlockTime);
        return scheduleId;
    }

    /**
     * @dev Executes a scheduled transfer if the unlock time has passed.
     * Only authorized executors can call this to prevent griefing or redundant calls.
     * @param scheduleId The ID of the transfer to execute.
     */
    function executeTransfer(uint256 scheduleId) 
        external 
        nonReentrant 
        whenNotPaused
        onlyExecutor 
        exists(scheduleId) 
        inStatus(scheduleId, Status.Waiting) 
    {
        ScheduledTransfer storage transfer = transfers[scheduleId];

        require(block.timestamp >= transfer.unlockTime, "TimeLockAgent: Time locked");

        // Update status first to prevent reentrancy (CEI pattern)
        transfer.status = Status.Executed;

        (bool success, ) = transfer.to.call{value: transfer.amount}("");
        require(success, "TimeLockAgent: Transfer failed");

        emit TransferExecuted(scheduleId, msg.sender, block.timestamp);
    }

    /**
     * @dev Executes multiple scheduled transfers in a single transaction.
     * @param scheduleIds An array of schedule IDs to execute.
     */
    function executeBatch(uint256[] calldata scheduleIds) 
        external 
        nonReentrant 
        whenNotPaused
        onlyExecutor
    {
        for (uint256 i = 0; i < scheduleIds.length; i++) {
            uint256 scheduleId = scheduleIds[i];
            ScheduledTransfer storage transfer = transfers[scheduleId];

            // Skip if doesn't exist, not waiting, or not yet unlockable
            if (transfer.createdAt == 0 || transfer.status != Status.Waiting || block.timestamp < transfer.unlockTime) {
                continue;
            }

            transfer.status = Status.Executed;
            (bool success, ) = transfer.to.call{value: transfer.amount}("");

            if (success) {
                emit TransferExecuted(scheduleId, msg.sender, block.timestamp);
            } else {
                // If one fails, we revert it back to Waiting so it can be retried individually
                transfer.status = Status.Waiting;
            }
        }
    }

    /**
     * @dev Cancels a scheduled transfer and refunds the creator.
     * Can only be called before execution and before expiration logic takes over (though expiration also refunds).
     * @param scheduleId The ID of the transfer to cancel.
     */
    function cancelTransfer(uint256 scheduleId) 
        external 
        nonReentrant
        exists(scheduleId)
        onlyCreator(scheduleId)
        inStatus(scheduleId, Status.Waiting)
    {
        require(block.timestamp < transfers[scheduleId].unlockTime, "TimeLockAgent: Too late to cancel, use expire if stale");

        ScheduledTransfer storage transfer = transfers[scheduleId];
        transfer.status = Status.Cancelled;

        (bool success, ) = transfer.creator.call{value: transfer.amount}("");
        require(success, "TimeLockAgent: Refund failed");

        emit TransferCancelled(scheduleId, block.timestamp);
    }

    /**
     * @dev Expires a stale transfer and refunds the creator.
     * Can be called by anyone (or restricted to creator/executor) if the grace period has passed.
     * This prevents funds from being stuck forever if the executor fails.
     * @param scheduleId The ID of the transfer to expire.
     */
    function expireTransfer(uint256 scheduleId) 
        external 
        nonReentrant
        exists(scheduleId)
        inStatus(scheduleId, Status.Waiting)
    {
        ScheduledTransfer storage transfer = transfers[scheduleId];
        require(block.timestamp > transfer.unlockTime + gracePeriod, "TimeLockAgent: Grace period not passed");

        transfer.status = Status.Expired;

        (bool success, ) = transfer.creator.call{value: transfer.amount}("");
        require(success, "TimeLockAgent: Refund failed");

        emit TransferExpired(scheduleId, block.timestamp);
    }

    // --- Admin Functions ---

    function setExecutor(address executor, bool allowed) external onlyOwner {
        isExecutor[executor] = allowed;
        emit ExecutorUpdated(executor, allowed);
    }

    function setGracePeriod(uint256 newGracePeriod) external onlyOwner {
        gracePeriod = newGracePeriod;
        emit GracePeriodUpdated(newGracePeriod);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // Fallback to receive native tokens (should usually be via schedule functions)
    receive() external payable {}
}

